var app=angular.module("myapp",["ngRoute"]);app.config(["$routeProvider",function(l){l.when("/home",{templateUrl:"html/home.html",controller:"homeControl"}).when("/hm1",{templateUrl:"html/money.html",controller:"hm1Control"}).when("/hm3",{templateUrl:"html/baby.html",controller:"hm3Control"}).when("/hm2",{templateUrl:"html/role.html",controller:"hm2Control"}).when("/hm4",{templateUrl:"html/prop.html",controller:"hm4Control"}).when("/hm6",{templateUrl:"html/collect.html"}).when("/hm5",{templateUrl:"html/jewelry.html",controller:"hm5Control"}).when("/hm7",{templateUrl:"html/date.html",controller:"hm7Control"}).when("/hm8",{templateUrl:"html/mysell.html"}).when("/search",{templateUrl:"html/search.html",controller:"searchControl"}).when("/auction",{templateUrl:"html/auction.html",controller:"auctionControl"}).when("/car",{templateUrl:"html/car.html",controller:"carControl"}).when("/car",{templateUrl:"html/car.html"}).when("/mine",{templateUrl:"html/mine.html"}).otherwise({redirectTo:"/home"})}]),app.controller("tab",["$scope",function(l){l.arr=[{cla:"tabbar-img1-1",word:"首页",index:"0",href:"#/home"},{cla:"tabbar-img2",word:"搜索",index:"1",href:"#/search"},{cla:"tabbar-img3",word:"购物车",index:"2",href:"#/car"},{cla:"tabbar-img4",word:"我的",index:"3",href:"#/mine"}],l.cl=function(t){for(var r=0;r<l.arr.length;r++)l.arr[r].cla="tabbar-img"+(r+1);l.arr[t].cla=l.arr[t].cla+"-1"}}]);